<?php

namespace Tests\Feature\GraphQL;

use App\Issue;
use App\Solution;
use App\User;
use Haxibiao\Breeze\GraphQLTestCase;
use Illuminate\Foundation\Testing\DatabaseTransactions;

class SolutionTest extends GraphQLTestCase
{
    use DatabaseTransactions;

    protected $user;

    protected function setUp(): void
    {
        parent::setUp();
        $this->user = $this->getRandomUser();

    }

    /* --------------------------------------------------------------------- */
    /* ------------------------------- Mutation ----------------------------- */
    /* --------------------------------------------------------------------- */

    /**
     * @group testAddSolutionMutation
     */
    public function testAddSolutionMutation()
    {
        $query = file_get_contents(__DIR__ . '/Solution/Mutation/addSolution.gql');

        $headers = $this->getRandomUserHeaders();

        $issue     = Issue::inRandomOrder()->first();
        $variables = [
            'issue_id' => $issue->id,
            'answer'   => "test hello world",
        ];

        $this->runGuestGQL($query, $variables, $headers);

        $issue     = Issue::inRandomOrder()->first();
        $variables = [
            'issue_id'   => $issue->id,
            'answer'     => "test hello world",
            'image_urls' => ['http://cos.dongdianyi.com/storage/img/159.jpg'],
        ];
        $this->runGuestGQL($query, $variables, $headers);

        $issue     = Issue::inRandomOrder()->first();
        $variables = [
            'issue_id' => $issue->id,
            'answer'   => "test hello world",
            'images'   => [''],
        ];

        $this->runGuestGQL($query, $variables, $headers);

    }
    /**
     * @group testDeleteSolutionMutation
     */
    public function testDeleteSolutionMutation()
    {
        $query = file_get_contents(__DIR__ . '/Solution/Mutation/deleteSolution.gql');
        $user  = User::inRandomorder()->first();
        $token = $user->api_token;

        $headers = [
            'Authorization' => 'Bearer ' . $token,
            'Accept'        => 'application/json',
        ];
        $issue = Issue::inRandomOrder()->first();
        $args  = [
            'user_id'  => $user->id,
            'issue_id' => $issue->id,
            'answer'   => "i'm a solution of issue",
        ];
        //用参数创建一个回答
        $solution  = Solution::firstOrCreate($args);
        $variables = [
            'id' => $solution->id,
        ];

        $this->runGuestGQL($query, $variables, $headers);
    }

    /* --------------------------------------------------------------------- */
    /* ------------------------------- Query ----------------------------- */
    /* ----------------------------up----------------------------------------- */
    /**
     * @group testSolutionsQuery
     */
    public function testSolutionsQuery()
    {

        $query = file_get_contents(__DIR__ . '/Solution/Query/solutionsQuery.gql');

        $issue = Issue::inRandomorder()->first();

        $variables = [
            'issue_id' => $issue->id,
        ];
        $this->runGuestGQL($query, $variables);

    }
    /**
     * @group testSolutionQuery
     */
    public function testSolutionQuery()
    {

        $query = file_get_contents(__DIR__ . '/Solution/Query/solutionQuery.gql');

        $solution = Solution::inRandomorder()->first();

        $variables = [
            'id' => $solution->id,
        ];
        $this->runGuestGQL($query, $variables);

    }

    /**
     * @group testMySolutionMutation
     */
    public function testMySolutionMutation()
    {
        $query = file_get_contents(__DIR__ . '/Solution/Query/mySolutionsQuery.gql');
        $user  = User::inRandomorder()->first();
        $token = $user->api_token;

        $headers = [
            'Authorization' => 'Bearer ' . $token,
            'Accept'        => 'application/json',
        ];
        $variables = [
            'user_id' => $user->id,
        ];

        $this->runGuestGQL($query, $variables, $headers);
    }
    protected function tearDown(): void
    {
        $this->user->forceDelete();

        parent::tearDown();
    }
}
